import * as React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Outlet } from 'react-router-dom';
import { useState, useEffect } from 'react';

import Landing from "./pages/public_routes/Landing";
import About from './pages/public_routes/About';
import TOC from './pages/public_routes/TOC';
import Projects from './pages/public_routes/Projects';
import NotFound from './pages/NotFound';
import LogIn from './pages/account/LogIn';
import Register from './pages/account/Register';
import ForgotPassword from './pages/account/ForgotPassword';
import ResetPassword from './pages/account/ResetPassword';

function App() {

  // States
  const [ authUser, setAuthUser ] = useState('');

  // useEffect
  useEffect(() => {
    let role = sessionStorage.role;
    const setUser = () => {
      if (role === 'admin') {
        setAuthUser('admin')
      } else if (role === 'employee') {
        setAuthUser('employee')
      } else if (role === 'client') {
        setAuthUser('client')
      } else {
        setAuthUser('public')
      }
    }
    setUser();
  },[])


  return (
    <Router>
      <Routes>
        {/* Public Route */}
        <Route path='/' element={<Landing />} />
        <Route path='/about' element={<About />} />
        <Route path='/terms' element={<TOC />} />
        <Route path='/projects' element={<Projects />} />
        <Route path='/login' element={<LogIn />} />
        <Route path='/register' element={<Register />} />
        <Route path='/forgotpassword' element={<ForgotPassword />} />
        <Route path='/resetpassword/:resetToken' element={<ResetPassword />} />
        <Route path='*' element={<NotFound />} />
        {/* Client Route */}
        {/* Employee Route */}
        {/* Admin Route */}
      </Routes>
    </Router>
  );
}

export default App;
