import './Login.css'
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { LockClosedIcon } from '@heroicons/react/outline';

const LogIn = (props) => {

  const [ email, setEmail ] = useState('');
  const [ password, setPassword ] = useState('');
  const [ error, setError ] = useState('');

  const navigate = useNavigate();

  const submitHandler = async (e) => {
    e.preventDefault();
    const config = {
      header: {
        "Content-Type": "application/json"
      }
    }
    try {
      const { data } = await axios.post(`${process.env.REACT_APP_SERVER_URL}/auth/login`, { email, password }, config)
      console.log(data);
      if(data.success === true) {
        sessionStorage.setItem('authToken', data.token);
        sessionStorage.setItem('role', data.role);
        sessionStorage.setItem('userId', data.userId);
        window.location.reload(false)
      }
    } catch (error) {
      console.log(error)
      setError('Please check your email and password again')
    }
  }

  useEffect(() => {
    if (sessionStorage.role === 'employee') {
      console.log('employee logged in')
      // redirect to employee dash
    } else if (sessionStorage.role === 'admin') {
      console.log('admin logged in')
      // redirect to admin dash
    } else if (sessionStorage.role === 'client') {
      navigate('/')
    }
  },[])

  return (
    <div className="min-h-full flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          {/* TODO: Change logo image */}
          <img
            className="mx-auto h-12 w-auto"
            src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg"
            alt="Workflow"
          />
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">Sign in to your account</h2>
          {!!error ? 
            <p className="mt-2 text-center text-sm font-medium text-indigo-500">
              {error}
            </p>
          :
          null
          }
        </div>
        <form className="mt-8 space-y-6" onSubmit={submitHandler}>
          <input type="hidden" name="remember" defaultValue="true" />
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="email-address" className="sr-only">
                Email address
              </label>
              <input
                id="email-address"
                name="email"
                type="email"
                autoComplete="email"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                placeholder="Email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <label htmlFor="password" className="sr-only">
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete="current-password"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                <LockClosedIcon className="h-5 w-5 text-indigo-500 group-hover:text-indigo-400" aria-hidden="true" />
              </span>
              Sign in
            </button>
          </div>

          <div className='flex items-center justify-center'>
            <p className='mr-4'>Don't have an account?</p>
            <Link to='/register' className='text-indigo-600 hover:text-indigo-400'>Create Account</Link>
          </div>

          <div className='flex items-center justify-center'>
            <Link to='/register' className='font-medium text-indigo-600 hover:text-indigo-400'>Forgot your password?</Link>
          </div>

          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="remember-me"
                name="remember-me"
                type="checkbox"
                className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
              />
              <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
                Remember me
              </label>
            </div>

            <div className="text-sm">
              <a href="#" className="font-medium text-indigo-600 hover:text-indigo-500">
                Forgot your password?
              </a>
            </div>
          </div>


        </form>
      </div>
    </div>
    // <div className="login-container">
    //   <div className='login-content-container'>
    //     <h3>Log In</h3>
    //     {!!error ? <p>{error}</p> : null}
    //     <form onSubmit={submitHandler}>
    //       <input
    //         type='email'
    //         value={email}
    //         onChange={(e) => setEmail(e.target.value)}
    //         placeholder="email"
    //       />
    //       <input
    //         type='password'
    //         value={password}
    //         onChange={(e) => setPassword(e.target.value)}
    //         placeholder="password"
    //       />
    //       <button type="submit">LogIn</button>
    //     </form>
    //     <div className='login_nav'>
    //       <ul>
    //         <li><Link to='/forgotpassword'>Forgot Password?</Link></li>
    //         <li>Don't have an account?</li>
    //         <li><Link to='/register'>Register</Link></li>
    //       </ul>
    //     </div>
    //   </div>
    // </div>
  );
}
export default LogIn;
